import React, { Component } from 'react'

export default class AddFilm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            title: "",
            type: "",
            director: "",
            length: 0,
            rate: 1,
            description: "",
            actors: "",

        }
    }
    onChangeTitle(e) {
        this.setState({
            title: e.target.value
        })
    }
    onChangeType(e) {
        this.setState({
            type: e.target.value
        })
    }
    onChangeDirector(e) {
        this.setState({
            director: e.target.value
        })
    }
    onChangeLenght(e) {
        this.setState({
            length: e.target.value
        })
    }
    onChangeRate(e) {
        this.setState({
            rate: e.target.value
        })
    }
    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        })
    }
    onChangeActors(e) {
        this.setState({
            actors: e.target.value
        })
    }
    onSubmit(e) {
        e.preventDeafault()

        const film = {
            title: this.state.title,
            type: this.state.type,
            director: this.state.director,
            length: this.state.length,
            rate: this.state.length,
            description: this.state.description,
            actors: this.state.actors
        }


        window.location = '/';
    }
    render() {
        return (
            <div>

            </div>
        )
    }
}
